// phone number regex reference https://stackoverflow.com/questions/18375929/validate-phone-number-using-javascript
// used ex7 solution as a base

import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View, FlatList } from 'react-native';
import { Button, CheckBox, Input, Text } from '@rneui/themed';
import * as Font from 'expo-font';
import FontAwesome from '@expo/vector-icons/FontAwesome';
import { useEffect, useState } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { StackActions } from '@react-navigation/native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import * as React from 'react';

const Tab = createBottomTabNavigator()
const Stack = createNativeStackNavigator()

async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font))
}

let initTasks = [
  {description: "Dishes", key: "1", completed: true, relatedTasks: [2]},
  {description: "Homework", key: "2", completed: true, relatedTasks: [3]},
  {description: "Walk dog", key: "3", completed: true, relatedTasks: [1]}
]

let loginInfo = []

async function AsyncLocalStorage(navigation) {
  const key = "loginData"
  loginInfo = [
  {username: "test", password: "Test1@"},
  {username: "aussieaussie", password: "0Ioi@1"}
  ]

try {
  const storedData = await AsyncStorage.getItem(key)
  const data = JSON.parse(storedData)

  if (data) {
    const { username, password } = data
    const authenticated = loginInfo.some((info) =>
    info.username === username && info.password === password)

  if (authenticated) {
    navigation.navigate("Todo", {name: "Todo"})
    } else {
      console.log ("Error: username or password not found")
    }
    } else {
      console.log ("Error: missing input")
    }
  } catch (error) {
      console.log ("Error")
}
}

export default function App() {
  return (
    <NavigationContainer>{
      <Tab.Navigator>
        <Tab.Screen name="Login" component={LoginScreen}></Tab.Screen>
        <Tab.Screen name="Register" component={RegisterScreen}></Tab.Screen>
        <Tab.Screen name="Todo" component={TodoHomeScreen} options={{ headerShown: false }}></Tab.Screen>
      </Tab.Navigator>
    }</NavigationContainer>
  );
}

function LoginScreen({navigation}) {
  let [unInput, setUnInput] = useState("")
  let [pInput, setPInput] = useState("")
  return (
    <View>
      <Text>Please fill out your information to log in</Text>
      <Input
        testID="login-username"
        placeholder="Username"
        onChangeText={setUnInput}
        value={unInput}>
      </Input>
      <Input
        testID="login-password"
        placeholder="Password"
        onChangeText={setPInput}
        value={pInput}>
      </Input>
      <Button testID="login-button" title="Login" style={styles.button}
      onPress={AsyncLocalStorage}/>
      <View style={styles.space} />
      <Button testID="register-button" title="Register" style={styles.button}
      onPress={() => navigation.navigate("Register", {name: "Register"})} />
    </View>
  )
}

function RegisterScreen() {
  let [checkNews, setCheckNews] = useState(false)
  let [firstName, setFirstName] = useState("")
  let [firstNameError, setFirstNameError] = useState("")
  let [lastName, setLastName] = useState("")
  let [lastNameError, setLastNameError] = useState("")
  let [username, setUsername] = useState("")
  let [password, setPassword] = useState("") 
  let [passwordError, setPasswordError] = useState("")
  let [phoneNumber, setPhoneNumber] = useState("")
  let [phoneError, setPhoneError] = useState("")
  let [email, setEmail] = useState("")
  let [emailError, setEmailError] = useState("")
  let [zipCode, setZipCode] = useState()
  let [zipError, setZipError] = useState("")
  function validate(input) {
    switch (input) {
      case 'firstName':
        console.log("Validating first name...")
        var first = /^[^\d=?\\/@#%^&*()]+$/
        if (firstName.match(first)) {
          setFirstNameError("")
        } else {
          setFirstNameError("Invalid first name")
        }
      break 
      case 'lastName':
        console.log("Validating last name...")
        if (!/^[^\d=?\\/@#%^&*()]+$/.test(lastName)) {
          setLastNameError("Invalid last name")
        } else {
          setLastNameError("")
        }
      break 
      case 'passWord':
        console.log("Validating password...")
          if (!/^([a-zA-Z\d\W]){5,}/.test(password)) {
          setPasswordError("Invalid password")
        } else {
          setPasswordError("")
        }
      break
      case 'eMail':
        console.log("Validating email...")
          if (!/.+@.+\..{2,3}/.test(email)) {
          setEmailError("Invalid email")
        } else {
          setEmailError("")
        }
      break
      case 'phone':
        console.log("Validating phone number...")
          if (!/^(\([0-9]{3}\) |[0-9]{3}-)[0-9]{3}-[0-9]{4}/.test(phoneNumber)) {
          setPhoneError("Invalid phone number")
        } else {
          setPhoneError("")
        }
      break
      case 'zipCode':
        console.log("Validating zip code...")
          if (!/^\d{5}/.test(zipCode)) {
          setEmailError("Invalid zip code")
        } else {
          setZipError("")
        }
      break
      default:
        console.log("Error: couldn't validate")
    }
  }
  return (
    <View>
      <Text>Please fill out your information to make an account</Text>
      <Input
        name="firstName"
        testID="firstname"
        placeholder="First Name"
        onChangeText={(text) => {setFirstName(text)}}
