import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import * as React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import FontAwesome from '@expo/vector-icons/FontAwesome';
import { useState, useCallback } from 'react';
import { CheckBox, Button, Input } from 'react-native-elements';

async function cacheFonts(fonts) {
  return fonts.map(async (font) => {await Font.loadAsync(font)})
}

function AsyncLocalStorage({key}) {
  key = "loginData"
  let loginInfo = [
  {username: "test", password: "Test1@"}
  ]
}

export default function App() {
  cacheFonts([FontAwesome.font])
  let [firstName, setFirstName] = useState("")
  let [firstNameError, setFirstNameError] = useState("")
  let [lastName, setLastName] = useState("")
  let [lastNameError, setLastNameError] = useState("")
  let [checkNews, setCheckNews] = useState(false)
  let [username, setUsername] = useState("")
  let [password, setPassword] = useState("") 
  let [passwordError, setPasswordError] = useState("")
  let [phoneNumber, setPhoneNumber] = useState("")
  let [phoneError, setPhoneError] = useState("")
  let [email, setEmail] = useState("")
  let [emailError, setEmailError] = useState("")
  let [zipCode, setZipCode] = useState()
  let [zipError, setZipError] = useState("")
  let [newsletter, setNewsletter] = useState(fasle)
  
let validateFirstName = useCallback(() => {
    console.log("Validating first name...")
    if (!/^[^\d=?\\/@#%^&*()]+$/.test(firstName)) {
      setLastNameError("Invalid last name")
    } else {
      setFirstNameError("")
    }
  }, [firstName])

  let validateLastName = useCallback(() => {
    console.log("Validating last name...")
    if (!/^[^\d=?\\/@#%^&*()]+$/.test(lastName)) {
      setLastNameError("Invalid last name")
    } else {
      setLastNameError("")
    }
  }, [lastName])

  let validatePassword = useCallback(() => {
    console.log("Validating password...")
    if (!/^([a-zA-Z\d\W]){5,}/.test(password)) {
      setPasswordError("Invalid password")
    } else {
      setPasswordError("")
    }
  }, [password])
 

  let validatePhone = useCallback(() => {
    console.log("Validating phone number...")
    if (!/^(\([0-9]{3}\) |[0-9]{3}-)[0-9]{3}-[0-9]{4}/.test(phoneNumber)) {
      setPhoneError("Invalid phone number")
    } else {
      setPhoneError("")
    }
  }, [phoneNumber])

  let validateEmail = useCallback(() => {
    console.log("Validating email...")
    if (!/.+@.+\..{2,3}/.test(email)) {
      setEmailError("Invalid email")
    } else {
      setEmailError("")
    }
  }, [email])

  let validateZip = useCallback(() => {
    console.log("Validating zip code...")
    if (!/^\d{5}/.test(zipCode)) {
      setEmailError("Invalid zip code")
    } else {
      setZipError("")
    }
  }, [zipCode])


  let updateInput = useCallback((text) => {
    setEmail(text)
  }, [])
  return (
    <NavigationContainer>{
      <View style={styles.container}>
        <Text>Open up App.js to start working on your app!</Text>
        <StatusBar style="auto" />
      </View>
    }</NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
