// used 

import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View, FlatList } from 'react-native';
import { Button, CheckBox, Input, Text } from '@rneui/themed';
import * as Font from 'expo-font';
import { useEffect, useState } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { StackActions } from '@react-navigation/native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import * as React from 'react';
import { SafeAreaView } from 'react-native-safe-area-context';

const Tab = createBottomTabNavigator()
const Stack = createNativeStackNavigator()

async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font))
}

const MENU_SCREEN = "home"
const EXERCISE_SCREEN = "exercise"
const DURATION_EXERCISE = "duration"
const REPITITION_EXERCISE = "repitition"
const RUNNING_EXERCISE = "running"

let exerciseList = [
  {type: DURATION_EXERCISE, name: "Running"},
  {type: DURATION_EXERCISE, name: "Plank"},
  {type: REPITITION_EXERCISE, name: "Push Ups"}
]

export default function App() {
let [currentScreen, setCurrentScreen] = useState(MENU_SCREEN)
    let [currentExercise, setCurrentExercise] = useState({})
    let screenComponent = undefined
    let buttonClick = useCallback ((exercise)=> {
      setCurrentExercise(exercise)
      setCurrentScreen(EXERCISE_SCREEN)
    })
    if (currentScreen === MENU_SCREEN) {
        screenComponent = <div>
            <h2>Exercise Menu</h2>
            <ul style={{listStyleType: "none"}}>   
                {exerciseList.map((exercise, index) => {
                return <li key={index}>
                    <button onClick={() => buttonClick(exercise)}>{exercise.name}</button>
                </li>
                })}
            </ul>
        </div>
    } else if (currentScreen === EXERCISE_SCREEN) {
        switch (currentExercise.type) {
            case DURRATION_EXERCISE: 
                screenComponent = <DurationExercise
                setMenuScreen={()=>setCurrentScreen(MENU_SCREEN)} 
                exercise={currentExercise}/> 
        break;
        case REPITITION_EXERCISE:
            screenComponent = <RepititionExercise 
            setMenuScreen={()=>setCurrentScreen(MENU_SCREEN)} 
            exercise={currentExercise}/>
        break;
        case RUNNING_EXERCISE:
            screenComponent = <RunningExercise 
            setMenuScreen={()=>setCurrentScreen(MENU_SCREEN)} 
            exercise={currentExercise}/>
        break;
        default:
            screenComponent = undefined
        }
    } 
    return (
      <NavigationContainer>{
        <Tab.Navigator>
          <Tab.Screen name="Home" component={HomeScreen}></Tab.Screen>
          <Tab.Screen name="Running Exercise" component={RunningScreen}></Tab.Screen>
          <Tab.Screen name="Push Ups Exercise" component={PushUpsScreen}></Tab.Screen>
          <Tab.Screen name="Plank Exercise" component={PlankScreen}></Tab.Screen>   
        </Tab.Navigator>
    }</NavigationContainer>
  );
}

function PlankScreen() {
  return (
    <View>
      <Text>Plank</Text>
    </View>
  )
}

function HomeScreen({navigation}) {
  let renderExercise = ({item}) => 
    <Button title={exercises.title} testID={`${exercises.title}-button`} style={styles.button}
    onPress={() => navigation.navigate("RunningScreen", {name: "Running"})} />
  return (
    <SafeAreaView>
      <View>
        <Text style={styles.heading}>Please select an exercise</Text>
          <FlatList data={exercises}
            renderItem={renderExercise}
            keyExtractor={(exercise) =>exercise.key}>
          </FlatList>
      </View>
    </SafeAreaView>
  )
}

function RunningScreen() {

  return (
    <View>
      <Text>Running</Text>
      <Button testID="register" title="Register" 
      style={styles.button}/>
    </View>
  )
}

function PushUpsScreen() {
let [exercise, setExercise] = useState("")
  return <Stack.Navigator initialRouteName="Home">
    <Stack.Screen name="Home">
      {(params) => (
        <HomeScreen {...params} exercise={exercise} setExercise={setExercise} />
      )}
    </Stack.Screen>
    <Stack.Screen name="Running">
        {(params) => (
        <RunningScreen {...params} exercise={exercise} setExercise={setExercise} />
      )}
    </Stack.Screen>
    <Stack.Screen name="Push Ups">
        {(paramss) => (
        <PushUpsScreen {...params} exercise={exercise} setExercise={setExercise} />
      )}
      <Stack.Screen name="Plank">
        {(params) => (
        <PlankScreen {...params} exercise={exercise} setExercise={setExercise} />
      )}
    </Stack.Screen>
    <Stack.Screen name="Related">
        {(params) => (
        <RelatedScreen {...params} exercise={exercise} setExercise={setExercise} />
      )}
    </Stack.Screen>
    </Stack.Screen>
  </Stack.Navigator>
}

function RelatedScreen({navigation, route, exercise, setExercise}) {
  let {description, completed, key, relatedTasks} = route.params.item
  useEffect(() => {
    navigation.setOptions({
      title: description === "" ? "No title" : description,
    })
  }, [navigation])
  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center"}}>
      <Text>Details Screen</Text>
      <Text>{description}</Text>
      {
        relatedTasks !== undefined && relatedTasks.length > 0 ?
        <>
        <Text>Related Tasks:</Text>
        {tasks.filter(task => relatedTasks.includes(task.key))
        .map(cTask => <Button key={cTask.key} title={cTask.description} 
          onPress={() => {
            navigation.dispatch(StackActions.push('Details', {item:cTask}));
          }}
          />)
          }
        </>
      : undefined}
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1, 
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  horizontal: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    padding: 10,
  },
  button: {
    alignItems: 'center',
    padding: 10,
  },
  space: {
    height: 10
  },
  heading: {
    fontSize: 30,
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
    padding: 20
  }
})
