// used lab6 as a base

import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View, FlatList } from 'react-native';
import { Button, CheckBox, Input, Text } from '@rneui/themed';
import * as Font from 'expo-font';
import { useEffect, useState, useCallback } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { StackActions } from '@react-navigation/native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import * as React from 'react';
import { SafeAreaView } from 'react-native-safe-area-context';
import Unorderedlist from 'react-native-unordered-list';

const Stack = createNativeStackNavigator()

async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font))
}

const HOME_SCREEN = "home"
const DURATION_EXERCISE = "duration"
const REPITITION_EXERCISE = "repitition"

let exerciseList = [
  {name: "Running", type: DURATION_EXERCISE, key: 1, related: [2]},
  {name: "Plank", type: DURATION_EXERCISE, key: 2, related: [1]},
  {name: "Push Ups", type: REPITITION_EXERCISE, key: 3, related: [4]},
  {name: "Squats", type: REPITITION_EXERCISE, key: 4, related: [3]}
]

export default function App() {
    return (
      <NavigationContainer>{
        <Stack.Navigator initialRouteName={HOME_SCREEN}>
          <Stack.Screen name={HOME_SCREEN} component={HomeScreen}></Stack.Screen>
          <Stack.Screen name={DURATION_EXERCISE} component={DurationScreen}></Stack.Screen>
          <Stack.Screen name={REPITITION_EXERCISE} component={RepetitionScreen}></Stack.Screen>
        </Stack.Navigator>
    }</NavigationContainer>
  );
}

function HomeScreen({navigation}) {
  let [exercise, setExercise] = useState(exerciseList)
  let renderExercise = ({item}) => 
    <Button title={item.name} testID={`${item.name}-button`} style={styles.button}
    onPress={() => navigation.navigate(item.type === DURATION_EXERCISE ? DURATION_EXERCISE : REPITITION_EXERCISE, {exercise: item})} />
    return (
    <SafeAreaView>
      <View>
        <Text style={styles.heading}>Please select an exercise</Text>
          <FlatList data={exerciseList}
            renderItem={renderExercise}
            keyExtractor={(exercise) =>exercise.key}>
          </FlatList>
      </View>
    </SafeAreaView>
  )
}

let currentTimer = 0
let resetTimer = 0
function DurationScreen({navigation, route}) {
    let [running, setRunning] = useState(false)
    let [timer, setTimer] = useState(0)
    let updateTimer = useCallback(() => {
        if(running){
            setTimer((timer) => timer+10)
        }
    }, [running, setInterval])
    useEffect(() => {
      if (running) {
        currentTimer = setInterval(updateTimer, 10)
      }  
        return () => clearInterval(currentTimer)
    }, [running, updateTimer])
    let start = useCallback(() => {
        setRunning(!running)
    }, [running])
    let reset = useCallback(() => {
        clearInterval(currentTimer)
        setTimer(resetTimer)
    })
    let mins = (Math.floor((timer / (1000*60)) % 60)).toString().padStart(2, "0")
    let secs = (Math.floor((timer / 1000) % 60)).toString().padStart(2, "0")
    let mills =  (timer % 1000).toString().padStart(3, "0")
    //let {title} = exercise 
    return (
        <View>
            <Text style={styles.heading}>{route.params.name}</Text>
            <Text style={{fontSize: "3em", fontFamily:"monospace", textAlign: 'center', padding: 10}}>Duration: {mins}:{secs}:{mills}</Text>
            <Button title="Start" style={styles.exerciseButton} onPress={start}></Button>
            <Button title="Reset" style={styles.exerciseButton} onPress={reset}></Button>
            <Button title="Home" style={styles.exerciseButton} onPress={() => navigation.goBack()}></Button>
        </View>
    )
}

function RepetitionScreen({navigation, exerciseList, route}) {
    let [count, setCount] = useState(0)
    console.log(route.params.item)
    let {key, related} = route.params.item
    console.log()
    // let name = "Push Ups"
    return <View>
      <Text style={styles.heading}>{route.params.name}</Text>
      <Text style={{fontSize: "3em", textAlign: 'center', padding: 10}}>{count}</Text>
      <Button title="Add" style={styles.exerciseButton} onPress={()=>setCount(count=>count+1)}></Button>
      <Button title="Reset" style={styles.exerciseButton} onPress={()=>setCount(0)}></Button>
      <Button title="Home" style={styles.exerciseButton} onPress={() => navigation.goBack()}></Button>
      {
        related !== undefined && related.length > 0 ?
        <>
        <Text>Related Exercise:</Text>
        {exerciseList.filter(exercise => related.includes(exercise.key))
        .map(cExercise => <Button key={cExercise.key} title={cExercise.name} 
          onPress={() => {
            navigation.dispatch(StackActions.push('Related', {item:cExercise}));
          }}
          />)
          }
        </>
      : undefined}
    </View>
}

const styles = StyleSheet.create({
  container: {
    flex: 1, 
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  horizontal: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    padding: 10,
  },
  button: {
    alignItems: 'center',
    padding: 10,
  },
  exerciseButton: {
    margin: 6
  },
  space: {
    height: 10
  },
  heading: {
    fontSize: 30,
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
    padding: 20
  }
})
