// phone number regex reference https://stackoverflow.com/questions/18375929/validate-phone-number-using-javascript
// used ex7 solution as a base

import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View, FlatList } from 'react-native';
import { Button, CheckBox, Input, Text } from '@rneui/themed';
import * as Font from 'expo-font';
import FontAwesome from '@expo/vector-icons/FontAwesome';
import { useEffect, useState } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { StackActions } from '@react-navigation/native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import * as React from 'react';

const Tab = createBottomTabNavigator()
const Stack = createNativeStackNavigator()

async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font))
}

let initTasks = [
  {description: "Dishes", key: "1", completed: true, relatedTasks: [2]},
  {description: "Homework", key: "2", completed: true, relatedTasks: [3]},
  {description: "Walk dog", key: "3", completed: true, relatedTasks: [1]}
]

export default function App() {
  return (
    <NavigationContainer>{
      <Tab.Navigator>
        <Tab.Screen name="login" component={LoginScreen}></Tab.Screen>
        <Tab.Screen name="register" component={RegisterScreen}></Tab.Screen>
        <Tab.Screen name="todo" component={TodoHomeScreen} options={{ headerShown: false }}></Tab.Screen>
      </Tab.Navigator>
    }</NavigationContainer>
  );
}

function LoginScreen() {
  return (
    <View>
      <Text>Sample</Text>
      <Input
        testID="login-username"
        placeholder="New task...">
      </Input>
      <Input
        testID="login-username"
        placeholder="New task...">
      </Input>
    </View>
  )
}

function RegisterScreen() {
  return <Text>Sample2</Text>
}

function TodoHomeScreen() {
  let [tasks, setTasks] = useState(initTasks)
  useEffect(() => {
    async function getValue() {
      const value = await AsyncStorage.getItem("@tasks")
      if (value === null) {
        console.log("Sorting tasks" + JSON.stringify(tasks))
        await AsyncStorage.setItem("@tasks", JSON.stringify(tasks))
      } else {
        let parsedValue = JSON.parse(value)
        console.log("Retrieving tasks")
        console.log(parsedValue)
        setTasks(JSON.parse(value))
      }
    }
    getValue()
  }, [])

  return <Stack.Navigator initialRouteName="Todo List">
    <Stack.Screen name="Todo List">
      {(props) => (
        <TodoScreen {...props} tasks={tasks} setTasks={setTasks} />
      )}
    </Stack.Screen>
    <Stack.Screen name="Details">
        {(props) => (
        <DetailsScreen {...props} tasks={tasks} setTasks={setTasks} />
      )}
    </Stack.Screen>
  </Stack.Navigator>
}

function DetailsScreen({navigation, route, setTasks, tasks}) {
  let {description, completed, key, relatedTasks} = route.params.item
  useEffect(() => {
    navigation.setOptions({
      title: description === "" ? "No title" : description,
    })
  }, [navigation])
  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center"}}>
      <Text>Details Screen</Text>
      <Text>{description}</Text>
      {
        relatedTasks !== undefined && relatedTasks.length > 0 ?
        <>
        <Text>Related Tasks:</Text>
        {tasks.filter(task => relatedTasks.includes(task.key))
        .map(cTask => <Button key={cTask.key} title={cTask.description} 
          onPress={() => {
            navigation.dispatch(StackActions.push('Details', {item:cTask}));
          }}
          />)
          }
        </>
      : undefined}
    </View>
  )
}

function TodoScreen({navigation, tasks, setTasks}) {
  cacheFonts([FontAwesome.font])
  let [input, setInput] = useState("")
  let updateTask = async (task) => {
    console.log(task)
    task.completed = !task.completed
    setTasks([...tasks])
    await AsyncStorage.setItem('@tasks', JSON.stringify(tasks))
  }
  let addTask = async () => {
    let maxKey = 0
    tasks.forEach(task => {
      if(task.key > maxKey) {
        maxKey = task.key
      }
    })
    let newTasks = [
      ...tasks,
      {
        description: input,
        completed: false,
        key: maxKey + 1,
      },
    ]
    setTasks(newTasks)
    console.log(newTasks)
    await AsyncStorage.setItem('@tasks', JSON.stringify(newTasks))
    setInput("")
  }
  let renderItem = ({item}) => {
    return (
      <View style={styles.horizontal}>
        <CheckBox textStyle={item.completed ? {
          textDecorationLine: "line-through",
          textDecorationStyle: "solid",
        } : undefined}
        title={item.description}
        checked={item.completed}
        onPress={() => updateTask(item)}/>
        <Button title="Details" onPress={() => navigation.navigate("Details", {item})}/>
      </View>
    )
  }
  return (
    <View style={[styles.container]}>
      <StatusBar style="auto" />
        <FlatList data={tasks} renderItem={renderItem} />
        <View style={[styles.horizontal]}>
          <Input
            onChangeText={setInput}
            value={input}
            placeholder="New task...">
          </Input>
          <Button title="Add Task" onPress={addTask} />
        </View>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1, 
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  horizontal: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    padding: 10,
  },
  button: {
    alignItems: 'center',
    backgroundColor: '#DDDDDD',
    padding: 10,
  },
})
