// used lab6 as a base

import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View, FlatList } from 'react-native';
import { Button, CheckBox, Input, Text } from '@rneui/themed';
import * as Font from 'expo-font';
import { useEffect, useState, useCallback } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { StackActions } from '@react-navigation/native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import * as React from 'react';
import { SafeAreaView } from 'react-native-safe-area-context';
import Unorderedlist from 'react-native-unordered-list';

const Stack = createNativeStackNavigator()

async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font))
}

const HOME_SCREEN = "home"
const EXERCISE_SCREEN = "exercise"
const DURATION_EXERCISE = "duration"
const REPITITION_EXERCISE = "repitition"

let exerciseList = [
  {type: DURATION_EXERCISE, name: "Running", key: 1},
  {type: DURATION_EXERCISE, name: "Plank", key: 2},
  {type: REPITITION_EXERCISE, name: "Push Ups", key: 3}
]

export default function App() {
  let [currentScreen, setCurrentScreen] = useState(HOME_SCREEN)
  let [currentExercise, setCurrentExercise] = useState({})
  let screenComponent = undefined
  let buttonClick = useCallback ((exercise)=> {
    setCurrentExercise(exercise)
    setCurrentScreen(EXERCISE_SCREEN)
    })
    if (currentScreen === HOME_SCREEN) {
        screenComponent = <View>
            <Text>Exercise Menu</Text>
            <Unorderedlist style={{listStyleType: "none"}}>   
                {exerciseList.map((exercise, index) => {
                return <Unorderedlist key={index}>
                    <Button onPress={() => buttonClick(exercise)}>{exercise.name}</Button>
                </Unorderedlist>
                })}
            </Unorderedlist>
        </View>
    } else if (currentScreen === EXERCISE_SCREEN) {
        switch (currentExercise.type) {
            case DURATION_EXERCISE: 
                screenComponent = <DurationScreen
                setMenuScreen={()=>setCurrentScreen(HOME_SCREEN)} 
                exercise={currentExercise}/> 
        break;
        case REPITITION_EXERCISE:
            screenComponent = <RepetitionScreen 
            setMenuScreen={()=>setCurrentScreen(HOME_SCREEN)} 
            exercise={currentExercise}/>
        break;
        default:
            screenComponent = undefined
        }
    } 
    return (
      <NavigationContainer>{
        <Stack.Navigator initialRouteName="Home">
          <Stack.Screen name={HOME_SCREEN} component={HomeScreen}></Stack.Screen>
          <Stack.Screen name={DURATION_EXERCISE} component={DurationScreen}></Stack.Screen>
          <Stack.Screen name={REPITITION_EXERCISE} component={RepetitionScreen}></Stack.Screen>
        </Stack.Navigator>
    }</NavigationContainer>
  );
}

function HomeScreen({navigation}) {
  let renderExercise = ({item}) => 
    <Button title={item.name} testID={`${item.name}-button`} style={styles.button}
    onPress={() => navigation.navigate(item.type === DURATION_EXERCISE ? "DurationScreen" : "RepititionScreen", {exercise: item})} />
  return (
    <SafeAreaView>
      <View>
        <Text style={styles.heading}>Please select an exercise</Text>
          <FlatList data={exerciseList}
            renderItem={renderExercise}
            keyExtractor={(exercise) =>exercise.key}>
          </FlatList>
      </View>
    </SafeAreaView>
  )
}

let currentTimer = 0
let resetTimer = 0
function DurationScreen({exercise, setMenuScreen}) {
    let [running, setRunning] = useState(false)
    let [timer, setTimer] = useState(0)
    let updateTimer = useCallback(() => {
        if(running){
            setTimer((timer) => timer+10)
        }
    }, [running])
    useEffect(() => {
        currentTimer = setInterval(updateTimer, 10)
        return () => clearInterval(currentTimer)
    }, [running, updateTimer])
    let start = useCallback(() => {
        setRunning(!running)
    }, [running])
    let reset = useCallback(() => {
        clearInterval(currentTimer)
        setTimer(resetTimer)
    })
    let mins = (Math.floor((timer / (1000*60)) % 60)).toString().padStart(2, "0")
    let secs = (Math.floor((timer / 1000) % 60)).toString().padStart(2, "0")
    //let {title} = exercise 
    return (
        <View>
            <Text style={styles.heading}>{exerciseList.name}</Text>
            <Text style={{fontSize: "3em", fontFamily:"monospace", textAlign: 'center'}}>Duration: {mins}:{secs}</Text>
            <Button title="Start" onPress={start}></Button>
            <Button title="Reset" onPress={reset}></Button>
            <Button title="Return" onPress={setMenuScreen}></Button>
        </View>
    )
}

function RepetitionScreen({exercise, setMenuScreen}) {
    let [count, setCount] = useState(0)
    function add(count, setcount) {
        setCount(count=>count+1)
    }
    return <View>
        <Text style={styles.heading}>{exerciseList.name}</Text>
        <Text style={{fontSize: "3em", textAlign: 'center'}}>{count}</Text>
        <Button title="Add" onPress={()=>setCount(count=>count+1)}></Button>
        <Button title="Reset" onPress={()=>setCount(0)}></Button>
        <Button title="Return" onPress={()=>setMenuScreen()}></Button>
    </View>
}

function PushUpsScreen() {
let [exercise, setExercise] = useState("")
  return <Stack.Navigator initialRouteName="Home">
    <Stack.Screen name="Home">
      {(params) => (
        <HomeScreen {...params} exercise={exercise} setExercise={setExercise} />
      )}
    </Stack.Screen>
    <Stack.Screen name="Running">
        {(params) => (
        <RunningScreen {...params} exercise={exercise} setExercise={setExercise} />
      )}
    </Stack.Screen>
    <Stack.Screen name="Push Ups">
        {(params) => (
        <PushUpsScreen {...params} exercise={exercise} setExercise={setExercise} />
      )}
      <Stack.Screen name="Plank">
        {(params) => (
        <PlankScreen {...params} exercise={exercise} setExercise={setExercise} />
      )}
    </Stack.Screen>
    <Stack.Screen name="Related">
        {(params) => (
        <RelatedScreen {...params} exercise={exercise} setExercise={setExercise} />
      )}
    </Stack.Screen>
    <Stack.Screen name="Repetition">
        {(params) => (
        <RepetitionScreen {...params} exercise={exercise} setExercise={setExercise} />
      )}
    </Stack.Screen>
    </Stack.Screen>
  </Stack.Navigator>
}

function RelatedScreen({navigation, route, exercise, setExercise}) {
  let {description, completed, key, relatedTasks} = route.params.item
  useEffect(() => {
    navigation.setOptions({
      title: description === "" ? "No title" : description,
    })
  }, [navigation])
  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center"}}>
      <Text>Details Screen</Text>
      <Text>{description}</Text>
      {
        relatedTasks !== undefined && relatedTasks.length > 0 ?
        <>
        <Text>Related Tasks:</Text>
        {tasks.filter(task => relatedTasks.includes(task.key))
        .map(cTask => <Button key={cTask.key} title={cTask.description} 
          onPress={() => {
            navigation.dispatch(StackActions.push('Details', {item:cTask}));
          }}
          />)
          }
        </>
      : undefined}
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1, 
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  horizontal: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    padding: 10,
  },
  button: {
    alignItems: 'center',
    padding: 10,
  },
  space: {
    height: 10
  },
  heading: {
    fontSize: 30,
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
    padding: 20
  }
})
