// used lab9 as a base
// question one reference - https://colorcodedlyrics.com/2022/12/30/ateez-halazia/
// question two refercne - https://colorcodedlyrics.com/2021/12/09/xdinary-heroes-egseudineoli-hieolojeu-happy-death-day/
// question three reference  - https://colorcodedlyrics.com/2021/12/23/stray-kids-broken-compass-gojangnan-nachimban/

import { StyleSheet, View, FlatList } from 'react-native';
import { Button, CheckBox, Input, Text, ButtonGroup } from '@rneui/themed';
import * as Font from 'expo-font';
import { useEffect, useState, useCallback } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { StackActions } from '@react-navigation/native';
import * as React from 'react';
import { SafeAreaView } from 'react-native-safe-area-context';

const Stack = createNativeStackNavigator()

async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font))
}

const QUESTION1_SCREEN = "Question1"
const QUESTION2_SCREEN = "Question2"
const QUESTION3_SCREEN = "Question3"
const SUMMARY_SCREEN = "Summary"

export default function App() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0)
  const [answeredCount, setAnsweredCount] = useState(0)
  let questions = [
    {
      "prompt": "Q1: Select the best answer: Who are you?",
      "type": "multiple-choice",
      "index": 0,
      "key": "0",
      "choices": [
        "It's just me, myself and I",
        "거울 속 비친 넌 누구인가",
        "기대 안에 기대 이 길의 뒤에",
        "All of the above",
    ],
      "correct": 3
    },
    {
      "prompt": "Q2: Fill in the blank: Happy ______ day",
      "type": "multiple-answer",
      "index": 1,
      "key": "1",
      "choices": [
        "Birthday",
        "Death",
        "Best",
        "Worst",
    ],
      "correct": [1,3]
    },
    {
      "prompt": `Q3: Is the following statment true or false?\n
        Stray Kids, STAY or none, we're gonna cross the finish line`,
      "type": "true-false",
      "index": 2,
      "key": "2",
        "choices": [
        "True",
        "False",
    ],
      "correct": 0
    },
  ]
    return (
      <NavigationContainer>{
        <Stack.Navigator initialRouteName={QUESTION1_SCREEN}>
          <Stack.Screen name="Question1" component={Question1Screen} initialParams={{questions: questions, currentQuestionIndex: currentQuestionIndex, 
            answeredCount: answeredCount}} options={{headerShown: false}}></Stack.Screen>
          <Stack.Screen name="Question2" component={Question2Screen} initialParams={{questions: questions, currentQuestionIndex: currentQuestionIndex, 
            answeredCount: answeredCount}} options={{headerShown: false}}></Stack.Screen>
          <Stack.Screen name="Question3" component={Question3Screen} initialParams={{questions: questions, currentQuestionIndex: currentQuestionIndex, 
            answeredCount: answeredCount}} options={{headerShown: false}}></Stack.Screen>
          <Stack.Screen name="Summary" component={SummaryScreen} initialParams={{questions: questions, currentQuestionIndex: currentQuestionIndex, 
            answeredCount: answeredCount}} options={{headerShown: false}}></Stack.Screen>
        </Stack.Navigator>
    }</NavigationContainer>
  );
}
function check({route}) {
    const { answeredCount } = route.params
    const prevAnsweredCount = React.useRef()
  let checkAnswer = useEffect(() => {
    if (selectChoice === currentQuestion.correct) {
      answeredCount = prevAnsweredCount + 1
    } else {
      answeredCount = prevAnsweredCount
    }
  })
  }
function Question1Screen({route, navigation}) {
  const { questions, currentQuestionIndex, answeredCount } = route.params
  let currentQuestion = questions[currentQuestionIndex]
  const [selectChoice, setSelectChoice] = useState(0)
  // function check() {
  // const prevAnsweredCount = React.useRef()
  // let checkAnswer = useEffect(() => {
  //   if (selectChoice === currentQuestion.correct) {
  //     answeredCount = prevAnsweredCount + 1
  //   } else {
  //     answeredCount = prevAnsweredCount
  //   }
  // })}
  const handleSelectChoice = (value) => {
    setSelectChoice(value)
  }
  const handleNextQuestion = () => {
    setSelectChoice((prevSelectChoice) => [
      ...prevSelectChoice,
      selectChoice
    ])
    setSelectChoice(null)
    if (currentQuestionIndex + 1 < questions.length) {
      navigation.push(QUESTION2_SCREEN, {questions, currentQuestionIndex: currentQuestionIndex + 1, checkAnswer})
    }
  }
     return (
    <SafeAreaView>
      <View>
        <Text style={styles.heading}>Quiz</Text>
        <Text style={styles.subheading}>{currentQuestion.prompt}</Text>
        <ButtonGroup buttons={currentQuestion.choices}
          selectChoice={selectChoice}
          onPress={(value) => {
            setSelectChoice(value)
          }}
          containerStyle={{ marginBottom: 20 }}
          vertical>
        </ButtonGroup>  
        <Button title="Next Question" testID="next-question" style={styles.button} onPress={() => navigation.push(QUESTION2_SCREEN, {questions, currentQuestionIndex: currentQuestionIndex + 1}) && check()}></Button>
      </View>
    </SafeAreaView>
  ) 
}

function Question2Screen({route, navigation}) {
  const { questions, currentQuestionIndex, answeredCount } = route.params
  let currentQuestion = questions[currentQuestionIndex]
  const [selectedChoices, setSelectedChoices] = useState([0, 1, 2, 3])
  const handleSelectedChoices = (value) => {
    setSelectedChoices(value)
  }
  const handleNextQuestion = () => {
    setSelectedChoices((prevSelectedChoices) => [
      ...prevSelectedChoices,
      selectedChoices
    ])
    setSelectedChoices(null)
    if (currentQuestionIndex + 1 < questions.length) {
      navigation.push(QUESTION3_SCREEN, {questions, currentQuestionIndex: currentQuestionIndex + 1, selectedChoices: selectedChoices, checkAnswer})
    }
  }
  return (
    <SafeAreaView>
      <View>
        <Text style={styles.heading}>Quiz</Text>
        <Text style={styles.subheading}>{currentQuestion.prompt}</Text>
        <ButtonGroup buttons={currentQuestion.choices}
          selectMultiple
          selectChoice={selectedChoices}
          onPress={(value) => {
            setSelectedChoices(value)
          }}
          containerStyle={{ marginBottom: 20 }}>
        </ButtonGroup>  
        <Button title="Next Question" testID="next-question" style={styles.button} onPress={() => navigation.push(QUESTION3_SCREEN, {questions, currentQuestionIndex: currentQuestionIndex + 1}) && check()}></Button>
      </View>
    </SafeAreaView>
  ) 
}

function Question3Screen({route, navigation}) {
  const { questions, currentQuestionIndex, answeredCount } = route.params
  let currentQuestion = questions[currentQuestionIndex]
  const [selectChoice, setSelectChoice] = useState(0)
  
  const handleSelectChoice = (value) => {
    setSelectChoice(value)
  }
  const handleNextQuestion = () => {
    setSelectChoice((prevSelectChoice) => [
      ...prevSelectChoice,
      selectChoice
    ])
    setSelectChoice(null)
    if (currentQuestionIndex + 1 < questions.length) {
      navigation.push(SUMMARY_SCREEN, {questions})
    }
  }
  return (
    <SafeAreaView>
      <View>
        <Text style={styles.heading}>Quiz</Text>
        <Text style={styles.subheading}>{currentQuestion.prompt}</Text>
        <ButtonGroup buttons={currentQuestion.choices}
          selectChoice={selectChoice}
          onPress={(value) => {
            setSelectChoice(value)
          }}
          containerStyle={{ marginBottom: 20 }}>
        </ButtonGroup>  
        <Button title="Finish" style={styles.button} onPress={() => navigation.push(SUMMARY_SCREEN, {questions, checkAnswer}) && check()}></Button>
      </View>
    </SafeAreaView>
  ) 
}

function SummaryScreen({route, navigation}) {
  const { questions, checkAnswer } = route.params
  return (
    <View>
      <Text style={styles.heading}>Summary</Text>
      <Text testID="total">Quiz Score:{answeredCount}/3</Text>
      <Button title="Restart" style={styles.button} onPress={() => navigation.push(QUESTION1_SCREEN, {questions})}></Button>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1, 
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  horizontal: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    padding: 10,
  },
  button: {
    alignItems: 'center',
    padding: 10,
  },
  buttonGroup: {
    backgroundColor : "#2089dc",
    color : "white",
    textAlign : "center",
    paddingVertical : 5,
    marginBottom : 10
  },
  space: {
    height: 10
  },
  heading: {
    fontSize: 30,
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
    padding: 20
  },
  subheading: {
    fontSize: 25,
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
    padding: 20
  }
})
