// phone number regex reference https://stackoverflow.com/questions/18375929/validate-phone-number-using-javascript
// used ex7 solution as a base

import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View, FlatList } from 'react-native';
import { Button, CheckBox, Input, Text } from '@rneui/themed';
import * as Font from 'expo-font';
import FontAwesome from '@expo/vector-icons/FontAwesome';
import { useEffect, useState } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { StackActions } from '@react-navigation/native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import * as React from 'react';

const Tab = createBottomTabNavigator()
const Stack = createNativeStackNavigator()

async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font))
}

let initTasks = [
  {description: "Dishes", key: "1", completed: true, relatedTasks: [2]},
  {description: "Homework", key: "2", completed: true, relatedTasks: [3]},
  {description: "Walk dog", key: "3", completed: true, relatedTasks: [1]}
]

function AsyncLocalStorage({key}) {
  key = "loginData"
  let loginInfo = [
  {username: "test", password: "Test1@"},
  {username: "aussieaussie", password: "0Ioi@1"}
  ]
}

export default function App() {
  return (
    <NavigationContainer>{
      <Tab.Navigator>
        <Tab.Screen name="Login" component={LoginScreen}></Tab.Screen>
        <Tab.Screen name="Register" component={RegisterScreen}></Tab.Screen>
        <Tab.Screen name="Todo" component={TodoHomeScreen} options={{ headerShown: false }}></Tab.Screen>
      </Tab.Navigator>
    }</NavigationContainer>
  );
}

function LoginScreen() {
  function login() {

  }
  return (
    <View>
      <Text>Sample</Text>
      <Input
        testID="login-username"
        placeholder="Username">
      </Input>
      <Input
        testID="login-password"
        placeholder="Password">
      </Input>
      <Button testID="login-button" title="Login" style={styles.button}
      onPress/>
      <View style={styles.space} />
      <Button testID="register-button" title="Register" style={styles.button}
      onPress={() => navigation.navigate("Register")} />
    </View>
  )
}

function RegisterScreen() {
  let input= "hello"
  function validate() {
    switch (input) {
      case 'firstName':
        console.log("Validating first name...")
        if (!/^[^\d=?\\/@#%^&*()]+$/.test(firstName)) {
          setLastNameError("Invalid last name")
        } else {
          setFirstNameError("")
        }
      break
      case 'lastName':
        console.log("Validating last name...")
        if (!/^[^\d=?\\/@#%^&*()]+$/.test(lastName)) {
          setLastNameError("Invalid last name")
        } else {
          setLastNameError("")
        }
      break
      case 'passWord':
        console.log("Validating password...")
          if (!/^([a-zA-Z\d\W]){5,}/.test(password)) {
          setPasswordError("Invalid password")
        } else {
          setPasswordError("")
        }
      break
      case 'eMail':
        console.log("Validating email...")
          if (!/.+@.+\..{2,3}/.test(email)) {
          setEmailError("Invalid email")
        } else {
          setEmailError("")
        }
      break
      case 'phone':
        console.log("Validating phone number...")
          if (!/^(\([0-9]{3}\) |[0-9]{3}-)[0-9]{3}-[0-9]{4}/.test(phoneNumber)) {
          setPhoneError("Invalid phone number")
        } else {
          setPhoneError("")
        }
      break
      case 'zipCode':
        console.log("Validating zip code...")
          if (!/^\d{5}/.test(zipCode)) {
          setEmailError("Invalid zip code")
        } else {
          setZipError("")
        }
      break
      default:
        console.log("Error")
    }
  }
  return (
    <View>
      <Text>Sample2</Text>
      <Input
        name="firstName"
        testID="firstname"
        placeholder="First Name"
        onBLur={validate}
        errorMessage="Error: first name must include letters or symbols only">
      </Input>
      <Input
        name="lastName"
        testID="lastname"
        placeholder="Last Name"
        errorMessage="Error: last name must include letters or symbols only">
      </Input>
      <Input
        name="userName"
        testID="username"
        placeholder="Username">
      </Input>
      <Input
        name="passWord"
        testID="password"
        placeholder="Password">
      </Input>
      <Input
        name="passWord"
        testID="confirmpassword"
        placeholder="Confirm Password"
        errorMessage="Error: password must include an upper case and lower case character, one number, and one symbol">
      </Input>
      <Input
        name="eMail"
        testID="email"
        placeholder="Email"
        errorMessage="Error: email must include an @ and a .">
      </Input>
      <Input
        name="phone"
        testID="phonenumber"
        placeholder="Phone Number"
        errorMessage="Error: phone number must be in the (xxx) xxx-xxxx format using digits only">
      </Input>
      <Input
        name="zipCode"
        testID="zip"
        placeholder="Zip Code"
        errorMessage="Error: zip code must be five digits">
      </Input>
      <CheckBox
        testID="newsletter"
        title="Sign up for our newsletter">
      </CheckBox>
      <Button testID="register" title="Register" 
      style={styles.button} disabled={true} />
    </View>
  )
}

function TodoHomeScreen() {
  let [tasks, setTasks] = useState(initTasks)
  useEffect(() => {
    async function getValue() {
      const value = await AsyncStorage.getItem("@tasks")
      if (value === null) {
        console.log("Sorting tasks" + JSON.stringify(tasks))
        await AsyncStorage.setItem("@tasks", JSON.stringify(tasks))
      } else {
        let parsedValue = JSON.parse(value)
        console.log("Retrieving tasks")
        console.log(parsedValue)
        setTasks(JSON.parse(value))
      }
    }
    getValue()
  }, [])

  return <Stack.Navigator initialRouteName="Todo List">
    <Stack.Screen name="Todo List">
      {(props) => (
        <TodoScreen {...props} tasks={tasks} setTasks={setTasks} />
      )}
    </Stack.Screen>
    <Stack.Screen name="Details">
        {(props) => (
        <DetailsScreen {...props} tasks={tasks} setTasks={setTasks} />
      )}
    </Stack.Screen>
  </Stack.Navigator>
}

function DetailsScreen({navigation, route, setTasks, tasks}) {
  let {description, completed, key, relatedTasks} = route.params.item
  useEffect(() => {
    navigation.setOptions({
      title: description === "" ? "No title" : description,
    })
  }, [navigation])
  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center"}}>
      <Text>Details Screen</Text>
      <Text>{description}</Text>
      {
        relatedTasks !== undefined && relatedTasks.length > 0 ?
        <>
        <Text>Related Tasks:</Text>
        {tasks.filter(task => relatedTasks.includes(task.key))
        .map(cTask => <Button key={cTask.key} title={cTask.description} 
          onPress={() => {
            navigation.dispatch(StackActions.push('Details', {item:cTask}));
          }}
          />)
          }
        </>
      : undefined}
    </View>
  )
}

function TodoScreen({navigation, tasks, setTasks}) {
  cacheFonts([FontAwesome.font])
  let [input, setInput] = useState("")
  let updateTask = async (task) => {
    console.log(task)
    task.completed = !task.completed
    setTasks([...tasks])
    await AsyncStorage.setItem('@tasks', JSON.stringify(tasks))
  }
  let addTask = async () => {
    let maxKey = 0
    tasks.forEach(task => {
      if(task.key > maxKey) {
        maxKey = task.key
      }
    })
    let newTasks = [
      ...tasks,
      {
        description: input,
        completed: false,
        key: maxKey + 1,
      },
    ]
    setTasks(newTasks)
    console.log(newTasks)
    await AsyncStorage.setItem('@tasks', JSON.stringify(newTasks))
    setInput("")
  }
  let renderItem = ({item}) => {
    return (
      <View style={styles.horizontal}>
        <CheckBox textStyle={item.completed ? {
          textDecorationLine: "line-through",
          textDecorationStyle: "solid",
        } : undefined}
        title={item.description}
        checked={item.completed}
        onPress={() => updateTask(item)}/>
        <Button title="Details" onPress={() => navigation.navigate("Details", {item})}/>
      </View>
    )
  }
  return (
    <View style={[styles.container]}>
      <StatusBar style="auto" />
        <FlatList data={tasks} renderItem={renderItem} />
        <View style={[styles.horizontal]}>
          <Input
            onChangeText={setInput}
            value={input}
            placeholder="New task...">
          </Input>
          <Button title="Add Task" onPress={addTask} />
        </View>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1, 
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  horizontal: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    padding: 10,
  },
  button: {
    alignItems: 'center',
    backgroundColor: '#DDDDDD',
    padding: 10,
  },
  space: {
    height: 10
  },
})
