// phone number regex reference https://stackoverflow.com/questions/18375929/validate-phone-number-using-javascript
// used ex7 solution as a base

import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View, FlatList } from 'react-native';
import { Button, CheckBox, Input, Text } from '@rneui/themed';
import * as Font from 'expo-font';
import FontAwesome from '@expo/vector-icons/FontAwesome';
import { useEffect, useState } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { StackActions } from '@react-navigation/native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import * as React from 'react';
import { SafeAreaView } from 'react-native-safe-area-context';

const Tab = createBottomTabNavigator()
const Stack = createNativeStackNavigator()

async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font))
}

let exercises = [
  {title: "Push Ups", type: "repetition", key: 1},
  {title: "Running", type: "duration", key: 2},
  {title: "Plank", type: "duration", key: 3}
]

export default function App() {
  return (
    <NavigationContainer>{
      <Tab.Navigator>
        <Tab.Screen name="Home" component={HomeScreen}></Tab.Screen>  
      </Tab.Navigator>
    }</NavigationContainer>
  );
}

function HomeScreen({navigation}) {
  let renderExercise = ({item}) => 
    <Button title={exercises.title} testID={`${exercises.title}-button`} style={styles.button}
    onPress={() => navigation.navigate("", {name: "Running"})} />
  return (
    <SafeAreaView>
      <View>
        <Text style={styles.heading}>Please select an exercise</Text>
          <FlatList data={exercises}
            renderItem={renderExercise}
            keyExtractor={(exercise) =>exercise.key}>
          </FlatList>
      </View>
    </SafeAreaView>
  )
}

{/* 
            <Button title="Plank" testID="plank-button" style={styles.button}
            data={exercises} renderItem={renderExercise}
            onPress={() => navigation.navigate("Register", {name: "Plank"})} /> */}

function RunningScreen() {

  return (
    <View>
      <Text>Please fill out your information to make an account</Text>
      <Button testID="register" title="Register" 
      style={styles.button} disabled={disableButton} 
      onPress={() => {validate(); 
      if (firstVal && lastVal && passVal && conPassVal && emailVal && phoneVal && zipVal) {
        addUser();
        setDisableButton(false)
      }}}/>
    </View>
  )
}

function PushUpsScreen() {
  let [tasks, setTasks] = useState(initTasks)
  useEffect(() => {
    async function getValue() {
      const value = await AsyncStorage.getItem("@tasks")
      if (value === null) {
        console.log("Sorting tasks" + JSON.stringify(tasks))
        await AsyncStorage.setItem("@tasks", JSON.stringify(tasks))
      } else {
        let parsedValue = JSON.parse(value)
        console.log("Retrieving tasks")
        console.log(parsedValue)
        setTasks(JSON.parse(value))
      }
    }
    getValue()
  }, [])

  return <Stack.Navigator initialRouteName="Home">
    <Stack.Screen name="Home">
      {(props) => (
        <HomeScreen {...props} tasks={tasks} setTasks={setTasks} />
      )}
    </Stack.Screen>
    <Stack.Screen name="Running">
        {(props) => (
        <RunningScreen {...props} tasks={tasks} setTasks={setTasks} />
      )}
    </Stack.Screen>
    <Stack.Screen name="Push Ups">
        {(props) => (
        <PushUpsScreen {...props} tasks={tasks} setTasks={setTasks} />
      )}
      <Stack.Screen name="Plank">
        {(props) => (
        <PlankScreen {...props} tasks={tasks} setTasks={setTasks} />
      )}
    </Stack.Screen>
    </Stack.Screen>
  </Stack.Navigator>
}

function DetailsScreen({navigation, route, setTasks, tasks}) {
  let {description, completed, key, relatedTasks} = route.params.item
  useEffect(() => {
    navigation.setOptions({
      title: description === "" ? "No title" : description,
    })
  }, [navigation])
  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center"}}>
      <Text>Details Screen</Text>
      <Text>{description}</Text>
      {
        relatedTasks !== undefined && relatedTasks.length > 0 ?
        <>
        <Text>Related Tasks:</Text>
        {tasks.filter(task => relatedTasks.includes(task.key))
        .map(cTask => <Button key={cTask.key} title={cTask.description} 
          onPress={() => {
            navigation.dispatch(StackActions.push('Details', {item:cTask}));
          }}
          />)
          }
        </>
      : undefined}
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1, 
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  horizontal: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    padding: 10,
  },
  button: {
    alignItems: 'center',
    padding: 10,
  },
  space: {
    height: 10
  },
  heading: {
    fontSize: 30,
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
    padding: 20
  }
})
